-- ----------------------------
-- Table structure for `tab_book`
-- ----------------------------
DROP TABLE IF EXISTS `tab_book`;
CREATE TABLE `tab_book` (
  `book_id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL,
  `brief` text,
  `catalogue` text,
  `appreciate` text,
  `photo` varchar(255) DEFAULT NULL,
  PRIMARY KEY (`book_id`)
) ENGINE=InnoDB AUTO_INCREMENT=117 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of tab_book
-- ----------------------------
INSERT INTO `tab_book` VALUES ('1', '中国共产党简史', '该书坚持辩证唯物主义和历史唯物主义立场观点方法，坚持解放思想、实事求是，严格遵守两个“历史决议”和习近平总书记关于党史的重要论述，忠实记录了一百年来中国共产党团结带领人民进行革命、建设、改革的光辉历程，充分反映了中国共产党为实现国家富强、民族振兴、人民幸福和人类文明进步事业作出的历史功绩，系统总结了党和国家事业不断从胜利走向胜利的宝贵经验，集中彰显了党在各个历史时期淬炼锻造的伟大精神。', '<div class=\"title\">第一章 中国共产党的创建和投身大革命的洪流</div>\r\n				<div>一、近代中国民族复兴的历史任务和各种力量的艰难探索</div>\r\n				<div>二、五四运动和马克思主义在中国的传播</div>\r\n				<div>三、中国共产党的成立和民主革命纲领的制定</div>\r\n				<div>四、第一次国共合作和大革命高潮的兴起</div>\r\n				<div>五、北伐战争和工农运动</div>\r\n				<div>六、国共合作的破裂和大革命的失败</div>\r\n				<div class=\"title\">第二章 掀起土地革命的风暴</div>\r\n				<div>一、以武装斗争反抗国民党的反动统治</div>\r\n				<div>二、和中国革命新道路的开辟</div>\r\n				<div>三、红军反“围剿”斗争的胜利和农村革命根据地的建设</div>\r\n				<div>四、九一八事变后的局势和中央红军长征的开始</div>\r\n				<div>五、遵义会议和红军长征的胜利</div>\r\n				<div>六、为建立抗日民族统一战线而斗争</div>\r\n				<div class=\"title\">第三章 全民族抗日战争的中流砥柱</div>\r\n				<div>一、党的全面抗战路线和持久抗战方针的制定</div>\r\n				<div>二、开展敌后抗日游击战争和坚持统一战线中的独立自主</div>\r\n				<div>三、坚持抗战、团结、进步的方针</div>\r\n				<div>四、巩固抗日根据地和推进抗日民主运动</div>\r\n				<div class=\"title\">第四章 夺取新民主主义革命的全国性胜利</div>\r\n				<div>五、加强党的建设、推进马克思主义中国化和开展整风运动</div>\r\n				<div>六、党的七大和确立思想为党的指导思想、抗日战争的最后胜利</div>\r\n				<div>一、重庆谈判和争取和平民主的斗争</div>\r\n				<div>二、粉碎国民党的军事进攻和第二条战线的形成</div>\r\n				<div>三、人民解放军转入战略进攻</div>\r\n				<div>四、夺取全国胜利纲领的制定实施和人民民主统一战线的巩固扩大</div>\r\n				<div>五、伟大的战略决战和国民党反动统治的覆灭</div>\r\n				<div>六、党的七届二中全会和筹建新中国</div>\r\n				<div class=\"title\">第五章 中华人民共和国的成立和社会主义制度的建立</div>\r\n				<div>一、中华人民共和国的成立和新生人民政权的巩固</div>\r\n				<div>二、抗美援朝战争、土地制度改革和其他民主改革</div>\r\n				<div>三、国民经济的恢复和各项建设的展开</div>\r\n				<div>四、党在过渡时期的总路线和有计划大规模经济建设的开始</div>\r\n				<div class=\"title\">第六章 社会主义建设的探索和曲折发展</div>\r\n				<div>一、党的八大和中国社会主义建设的良好开端</div>\r\n				<div>二、社会主义道路的艰辛探索</div>\r\n				<div>三、社会主义建设在曲折中发展</div>\r\n				<div>五、社会主义改造的基本完成和社会主义制度的建立</div>\r\n				<div class=\"title\">第七章 伟大历史转折和中国特色社会主义的开创</div>\r\n				<div>一、党的十一届三中全会实现伟大历史转折</div>\r\n				<div>二、拨乱反正任务的完成</div>\r\n				<div>三、农村改革、创办经济特区和改革开放的起步</div>\r\n				<div>四、党的十二大和社会主义现代化建设的全面展开</div>\r\n				<div>五、党的十三大和党在社会主义初级阶段基本路线的确立</div>\r\n				<div>六、国防战略的调整</div>\r\n				<div>七、“一国两制”方针的形成</div>\r\n				<div>八、外交方针政策的调整</div>\r\n				<div>九、经受政治风波的考验和治理整顿的完成</div>\r\n				<div class=\"title\">第八章 把中国特色社会主义全面推向</div>21世纪\r\n				<div>一、党的十四大和建立社会主义市场经济体制</div>\r\n				<div>二、加强宏观调控与经济发展实现“软着陆”</div>\r\n				<div>三、党的十五大和确立理论为党的指导思想、改革开放的深入推进</div>\r\n				<div>四、跨世纪发展战略的制定与实施</div>\r\n				<div>五、政治文明、先进文化建设和人民生活实现总体小康</div>\r\n				<div>六、积极推进中国特色军事变革</div>\r\n				<div>七、香港澳门回归祖国和两岸交流扩大</div>\r\n				<div>八、推动构建全方位多层次对外关系新格局</div>\r\n				<div>九、推进党的建设新的伟大工程</div>\r\n				<div>十、南方谈话</div>\r\n				<div class=\"title\">第九章 在新的形势下坚持和发展中国特色社会主义</div>\r\n				<div>一、党的十六大和确立“三个代表”重要思想为党的指导思想、提出全面建设小康社会的纲领</div>\r\n				<div>二、推动经济社会科学发展</div>\r\n				<div>三、党的十七大和全面建设小康社会新部署</div>\r\n				<div>四、应对重大挑战和深化改革开放</div>\r\n				<div>五、履行新世纪新阶段军队历史使命</div>\r\n				<div>六、推进“一国两制”实践和祖国和平统一大业</div>\r\n				<div>七、坚持和平发展合作</div>\r\n				<div>八、提高党的建设科学化水平和确立科学发展观为党的指导思想</div>\r\n				<div>九、国家制度和治理体系建设迈出新步伐</div>\r\n				<div class=\"title\">第十章 中国特色社会主义进人新时代</div>\r\n				<div>一、党的十八大和实现中华民族伟大复兴的中国梦</div>\r\n				<div>二、统筹推进“五位一体”总体布局</div>\r\n				<div>三、协调推进“四个全面”战略布局</div>\r\n				<div>四、全面推进国防和军队现代化建设</div>\r\n				<div>五、坚持“一国两制”和推进祖国统一</div>\r\n				<div>六、全面推进中国特色大国外交和推动构建人类命运共同体</div>\r\n				<div>七、党的十九大和确立新时代中国特色社会主义思想为党的指导思想、决胜全面建成小康社会</div>\r\n				<div>八、坚持党的全面领导和提高党的建设质量</div>\r\n				<div>十、在应对风险挑战中推进各项事业</div>\r\n				<div>十一、全面建成小康社会和开启全面建设社会主义现代化国家新征程</div>', '<div class=\"para\" label-module=\"para\" data-pid=\"11\">该书充分反映了习近平总书记关于党的历史的重要论述精神。党的十八大以来，习近平总书记围绕党的历史发表的一系列重要论述，是习近平新时代中国特色社会主义思想的重要组成部分，是中国共产党的理论成果，也集中反映了党史研究的进展，是《中国共产党简史》编写的根本遵循。《中国共产党简史》对建党建军、抗日战争、抗美援朝、社会主义建设中的曲折和改革开放等的叙述和评价，充分吸收了习近平总书记重要论述精神，高屋建瓴、鞭辟入里，既实事求是地讲出历史的本然，又明白晓畅地讲出历史的所以然。</div><div class=\"para\" label-module=\"para\" data-pid=\"12\">该书充分反映了中国选择中国特色社会主义道路的历史必然性。《中国共产党简史》讲清了以毛泽东同志为主要代表的中国共产党人艰辛探索、最终找到正确道路，从而推翻三座大山、建立新中国的历史；讲清了社会主义制度建立之后，党带领人民积极探索社会主义建设道路的历史；讲清了十一届三中全会以后中国共产党找到、坚持并不断开拓中国特色社会主义道路的历史；讲清了党的十八大以来，在以习近平同志为核心的党中央坚强领导下，各项事业取得历史性成就、发生历史性变革的历史。《中国共产党简史》以无可辩驳的事实阐明：道路问题是最根本的问题，中国特色社会主义道路是历史的选择，人民的选择。</div><div class=\"para\" label-module=\"para\" data-pid=\"13\">该书充分反映了百年党史是党带领人民群众共同创造的历史。《中国共产党简史》展现了中国共产党自诞生之日起就把为中国人民谋幸福、为中华民族谋复兴作为自己的初心和使命的历史。为此，党把中国人民团结凝聚成万众一心的磅礴力量，进行了前赴后继的不懈奋斗。《中国共产党简史》展现了人民群众在党的领导下艰苦奋斗的历史，介绍了他们中间涌现出的无数英雄和锻造的伟大精神。通览全书，雷锋、王进喜、“两弹一星”科学家等等，一个个光辉的名字彪炳史册；伟大抗战精神、伟大抗美援朝精神、伟大抗疫精神等等，一个个伟大的精神历久弥新。《中国共产党简史》以无可辩驳的事实阐明：中国之所以取得举世瞩目的伟大成就，是由于中国共产党的领导，是由于党始终同人民群众保持血肉联系，依靠人民创造了历史伟业。</div><div class=\"para\" label-module=\"para\" data-pid=\"14\">该书充分反映了党的光辉历史是不断推进马克思主义中国化的历史。《中国共产党简史》再现了毛泽东思想、邓小平理论、“三个代表”重要思想、科学发展观和习近平新时代中国特色社会主义思想的形成发展历程，以无可辩驳的事实阐明：中国共产党之所以能够把革命引向胜利，一条根本性的经验就是，必须坚持把马克思主义基本原理同中国具体实际结合起来，不断推进马克思主义中国化。</div><div class=\"para\" label-module=\"para\" data-pid=\"15\">该书充分反映了党的光辉历史是一部党不断进行自我革命的历史。《中国共产党简史》重点突出地反映了党加强自身建设的历史，对于党的代表大会如何推进中国革命、建设和改革，党章的修改怎样体现党的建设的新要求，都进行了浓墨重彩的描述。《中国共产党简史》以无可辩驳的事实阐明：中国共产党之所以能够在各种政治力量的反复较量中脱颖而出，能够始终走在时代前列、成为中国人民和中华民族的主心骨，根本原因就在于中国共产党始终保持了自我革命精神，不断把党建设得更加坚强有力。\r\n\r\n</div>', 'https://gimg2.baidu.com/image_search/src=http%3A%2F%2Fp9.itc.cn%2Fq_70%2Fimages03%2F20210423%2Fde7d2becd9d744ffad9cb00b589db902.png');
INSERT INTO `tab_book` VALUES ('2', '论中国共产党历史', '历史是最好的教科书。党的十八大以来，习近平同志围绕中国共产党历史发表了一系列重要论述，系统回顾我们党团结带领中国人民不懈奋斗的光辉历程，深入总结党在各个历史时期创造的理论成果、积累的宝贵经验、铸就的伟大精神，深刻阐明党为中华民族作出的伟大贡献、为解决人类问题提供的中国智慧中国方案，展望党和人民事业发展的光明前景。这些重要论述立意高远，内涵丰富，思想深刻，是习近平新时代中国特色社会主义思想的重要组成部分。习近平同志《论中国共产党历史》的出版发行，对于广大干部群众学好党的历史，增强“四个意识”、坚定“四个自信”、做到“两个维护”，决胜全面建成小康社会、开启全面建设社会主义现代化国家新征程、实现中华民族伟大复兴的中国梦，具有十分重要的指导意义。', '<div>实现中华民族伟大复兴是中华民族近代以来最伟大的梦想</div>\r\n<div>正确认识改革开放前和改革开放后两个历史时期</div>\r\n<div>知史爱党，知史爱国</div>\r\n<div>学习党史、国史是坚持和发展中国特色社会主义的必修课</div>\r\n<div>中国革命历史是最好的营养剂</div>\r\n<div>讲好中国共产党的故事</div>\r\n<div>革命精神是党和国家的宝贵财富</div>\r\n<div>革命老区是党和人民军队的根</div>\r\n<div>在纪念毛泽东同志诞辰一百二十周年座谈会上的讲话</div>\r\n<div>崇尚英雄、学习英雄、关爱英雄</div>\r\n<div>在纪念邓小平同志诞辰一百一十周年座谈会上的讲话</div>\r\n<div>在中国实行人民代表大会制度，是中国人民在人类政治制度史上的伟大创造</div>\r\n<div>古田会议奠基的我军政治工作对我军生存发展起到了决定性作用</div>\r\n<div>继续从延安精神中汲取力量</div>\r\n<div>总结党的历史经验，牢固树立“四个意识\"</div>\r\n<div>把红色基因传承好，把红色江山世世代代传下去</div>\r\n<div>让井冈山精神放射出新的时代光芒</div>\r\n<div>在庆祝中国共产党成立九十五周年大会上的讲话</div>\r\n<div>在纪念红军长征胜利八十周年大会上的讲话</div>\r\n<div>学好党史、新中国史、改革开放史、社会主义发展史</div>\r\n<div>在庆祝中国人民解放军建军九十周年大会上的讲话</div>\r\n<div>新时代中国共产党的历史使命</div>\r\n<div>走得再远都不能忘记来时的路</div>\r\n<div>深刻总结经济特区建设的宝贵经验</div>\r\n<div>在纪念马克思诞辰二百周年大会上的讲话</div>\r\n<div>在庆祝改革开放四十周年大会上的讲话</div>\r\n<div>在纪念五四运动一百周年大会上的讲话</div>\r\n<div>中国共产党的伟大革命精神跨越时空、永不过时</div>\r\n<div>党在陕甘宁地区的革命活动，建立了光照千秋的历史功绩</div>\r\n<div>以“赶考\"的清醒和坚定答好新时代的答卷</div>\r\n<div>了解历史才能看得远，永葆初心才能走得远</div>\r\n<div>人民政协在建立新中国和社会主义革命、建设、改革各个历史时期发挥了十分重要的作用</div>\r\n<div>在庆祝中华人民共和国成立七十周年大会上的讲话</div>\r\n<div>始终做到初心如磐、使命在肩</div>\r\n<div>在纪念中国人民抗日战争暨世界反法西斯战争胜利七十五周年座谈会上的讲话</div>\r\n<div>牢记初心使命走好新时代长征路</div>\r\n<div>兴办经济特区是党和国家为推进改革开放和社会主义现代化建设进行的伟大创举</div>\r\n<div>在纪念中国人民志愿军抗美援朝出国作战七十周年大会上的讲话</div>\r\n<div>中华民族伟大复兴历史进程的大跨越</div>\r\n<div>在浦东开发开放三十周年庆祝大会上的讲话</div>', null, 'https://p3.img.cctvpic.com/photoAlbum/page/performance/img/2021/5/27/1622104038230_275.jpg');
INSERT INTO `tab_book` VALUES ('3', '习近平新时代中国特色社会主义思想学习问答', '为把学习贯彻习近平新时代中国特色社会主义思想不断引向深入，中央宣传部组织编写了《习近平新时代中国特色社会主义思想学习问答》一书，已由学习出版社、人民出版社联合出版，即日起在全国发行。\r\n\r\n《问答》紧跟实践发展步伐，聚焦理论热点难点，回应干部群众关切，以问答体的形式全面系统、深入浅出阐述了习近平新时代中国特色社会主义思想的基本精神、基本内容、基本要求，有助于广大党员干部群众更加深入学习领会党的创新理论，更加自觉用以武装头脑、指导实践、推动工作。《问答》共分7个板块、100个问题，内容丰富、形式新颖，图文并茂、通俗易懂，是深入学习贯彻习近平新时代中国特色社会主义思想的重要辅助读物。', '<div>一、思想之旗领航向</div>\r\n<div>如何理解习近平新时代中国特色社会主义思想是当代中国马克思主义、21世纪马克思主义？</div>\r\n<div>如何理解习近平新时代中国特色社会主义思想创立的时代背景？</div>\r\n<div>如何理解习近平新时代中国特色社会主义思想是系统科学的理论体系？</div>\r\n<div>如何理解习近平新时代中国特色社会主义思想是经过实践检验、富有实践伟力的强大武器？</div>\r\n<div>如何理解习近平新时代中国特色社会主义思想是坚持和运用辩证唯物主义和历史唯物主义的光辉典范？</div>\r\n<div>如何理解毫不动摇坚持用习近平新时代中国特色社会主义思想武装全党、教育人民？</div>\r\n<div>二、人间正道开新篇</div>\r\n<div>中国特色社会主义进入新时代，新在哪里？</div>\r\n<div>世界正经历百年未有之大变局，变在何处？</div>\r\n<div>为什么说社会主要矛盾发生变化，但我国仍处于并将长期处于社会主义初级阶段？</div>\r\n<div>为什么说中国特色社会主义是历史的选择、人民的选择？</div>\r\n<div>为什么说中国特色社会主义是社会主义，不是别的什么主义？</div>\r\n<div>为什么说改革开放前后两个历史时期不能相互否定？</div>\r\n<div>为什么必须坚定中国特色社会主义道路自信、理论自信、制度自信、文化自信？</div>\r\n<div>如何理解“中国之治”背后的制度密码？</div>\r\n<div>为什么说新时代坚持和发展中国特色社会主义是一场伟大社会革命？</div>\r\n<div>为什么要丰富和发展</div>21世纪中国的马克思主义？\r\n<div>三、初心砥柱天地间</div>\r\n<div>我们党作为百年大党，为什么必须始终牢记初心和使命？</div>\r\n<div>对党员干部来讲，为什么说不忘初心、牢记使命是一辈子的事？</div>\r\n<div>为什么说以人民为中心是我们党的根本立场？</div>\r\n<div>为什么说人民对美好生活的向往就是我们的奋斗目标？</div>\r\n<div>为什么说人民是我们党执政的最大底气？</div>\r\n<div>为什么说只有中国共产党才能肩负起实现中华民族伟大复兴的历史使命？</div>\r\n<div>为什么说实现伟大梦想必须进行伟大斗争、建设伟大工程、推进伟大事业？</div>\r\n<div>四、“四个全面”擘宏图</div>\r\n<div>如何理解全面建成小康社会是中华民族伟大复兴向前迈出的新的一大步？</div>\r\n<div>如何理解打赢脱贫攻坚战谱写了人类反贫困历史新篇章？</div>\r\n<div>如何理解我国将进入全面建设社会主义现代化国家的新发展阶段？</div>\r\n<div>如何理解中国式现代化是人类历史上前所未有的大变革？</div>\r\n<div>如何理解当前和今后一个时期我国发展仍然处于重要战略机遇期？</div>\r\n<div>为什么说党的十一届三中全会是划时代的，党的十八届三中全会也是划时代的？</div>\r\n<div>为什么要提出全面深化改革总目标？</div>\r\n<div>如何理解全面深化改革是有方向、有立场、有原则的？</div>\r\n<div>为什么说全面深化改革既要摸着石头过河，又要加强顶层设计？</div>\r\n<div>如何理解要积极争当改革促进派、实干家？</div>\r\n<div>如何理解中国开放的大门只会越开越大？</div>\r\n<div>为什么说一些“回头浪”的逆流挡不住经济全球化的历史潮流？</div>\r\n<div>如何理解全面依法治国是国家治理的一场深刻革命？</div>\r\n<div>如何理解中国特色社会主义法治道路是建设社会主义法治国家的唯一正确道路？</div>\r\n<div>如何理解公正司法是维护社会公平正义的最后一道防线？</div>\r\n<div>为什么说我们坚持的依宪治国、依宪执政，与西方所谓的“宪政”本质上是不同的？</div>\r\n<div>为什么说“党大还是法大”是个伪命题？</div>\r\n<div>为什么说全面从严治党是一场伟大的自我革命？</div>\r\n<div>为什么说我们党是马克思主义执政党，同时是马克思主义革命党？</div>\r\n<div>如何理解政治建设是党的根本性建设？</div>\r\n<div>为什么说理想信念是共产党人精神上的“钙”？</div>\r\n<div>为什么要着力培养忠诚干净担当的高素质干部？</div>\r\n<div>为什么要驰而不息反对形式主义、官僚主义？</div>\r\n<div>为什么要把纪律的螺丝拧得紧而又紧？</div>\r\n<div>如何理解要把权力关进制度的笼子里？</div>\r\n<div>为什么要一体推进不敢腐、不能腐、不想腐？</div>\r\n<div>五、“五位一体”谱华章</div>\r\n<div>为什么说新发展理念开拓了中国特色社会主义政治经济学新境界？</div>\r\n<div>如何理解我国社会主义基本经济制度的重大发展？</div>\r\n<div>如何理解使市场在资源配置中起决定性作用，更好发挥政府作用？</div>\r\n<div>如何理解我国经济已由高速增长阶段转向高质量发展阶段？</div>\r\n<div>为什么要把推进供给侧结构性改革作为经济发展的主线？</div>\r\n<div>为什么说任何想把公有制经济否定掉或想把非公有制经济否定掉的观点都是错误的？</div>\r\n<div>为什么要完善关键核心技术攻关的新型举国体制？</div>\r\n<div>如何理解推动形成主体功能明显、优势互补、高质量发展的区域经济布局？</div>\r\n<div>如何理解把乡村振兴战略作为新时代“三农”工作总抓手？</div>\r\n<div>如何理解构建以国内大循环为主体、国内国际双循环相互促进的新发展格局？</div>\r\n<div>为什么说走中国特色社会主义政治发展道路，不能指望搬来政治制度上的“飞来峰”？</div>\r\n<div>如何理解中国特色社会主义民主是个新事物，也是个好事物？</div>\r\n<div>如何理解人民民主是一种“全过程民主”？</div>\r\n<div>如何理解协商民主是我们独特的、独有的、独到的民主形式？</div>\r\n<div>为什么说文化自信是更基础、更广泛、更深厚的自信？</div>\r\n<div>为什么要坚持马克思主义在意识形态领域指导地位的根本制度？</div>\r\n<div>为什么要着力培养担当民族复兴大任的时代新人？</div>\r\n<div>如何理解和把握伟大抗疫精神？</div>\r\n<div>为什么要旗帜鲜明反对西方所谓的“普世价值”？</div>\r\n<div>为什么说新闻舆论工作必须坚持党性原则、坚持党管媒体？</div>\r\n<div>为什么说中华优秀传统文化不能丢，丢了就割断了精神命脉？</div>\r\n<div>为什么说文艺不能在市场经济大潮中迷失方向？</div>\r\n<div>如何理解“过不了互联网这一关，就过不了长期执政这一关”？</div>\r\n<div>如何讲好中国故事，解决好“失语就要挨骂”的问题？</div>\r\n<div>为什么解决民生问题要多做雪中送炭的工作，少做花上垒花的虚功？</div>\r\n<div>为什么说立德树人是新时代教育的根本任务？</div>\r\n<div>为什么要毫不动摇把公益性写在医疗卫生事业的旗帜上？</div>\r\n<div>如何加强和创新社会治理，实现共建共治共享？</div>\r\n<div>为什么要像保护眼睛一样保护生态环境，像对待生命一样对待生态环境？</div>\r\n<div>如何理解绿水青山就是金山银山的理念？</div>\r\n<div>如何理解推动形成绿色发展方式和生活方式，是发展观的一场深刻革命？</div>\r\n<div>如何理解建设美丽中国要实行最严格的生态环境保护制度？</div>\r\n<div>六、千秋伟业强基石</div>\r\n<div>为什么说保证国家安全是头等大事？</div>\r\n<div>为什么说政治安全是国家安全的根本？</div>\r\n<div>为什么要增强忧患意识、防范风险挑战，尤其要高度警惕“黑天鹅”和“灰犀牛”事件？</div>\r\n<div>如何把人民军队建设成为世界一流军队？</div>\r\n<div>为什么要坚持党对人民军队的绝对领导？</div>\r\n<div>如何理解新时代人民军队使命任务？</div>\r\n<div>为什么说“一国两制”是中国特色社会主义的一个伟大创举？</div>\r\n<div>如何理解中央对香港、澳门的全面管治权？</div>\r\n<div>如何理解只有构建人类命运共同体才是人间正道？</div>\r\n<div>为什么说中国绝不会走“国强必霸”的路子？</div>\r\n<div>如何理解要全面推进中国特色大国外交，打造覆盖全球的“朋友圈”？</div>\r\n<div>为什么说共建“一带一路”是要建设同世界各国共享机遇、共谋发展的“百花园”？</div>\r\n<div>七、万山磅礴有主峰</div>\r\n<div>为什么说中国共产党领导是中国特色社会主义最本质的特征？</div>\r\n<div>如何理解党政军民学，东西南北中，党是领导一切的？</div>\r\n<div>如何理解党的领导制度是我国的根本领导制度？</div>\r\n<div>如何理解必须坚决维护习近平总书记党中央的核心、全党的核心地位？</div>\r\n<div>如何理解必须坚决维护党中央权威和集中统一领导？</div>\r\n<div>如何理解民主集中制是我们党的根本组织原则和领导制度？</div>\r\n<div>为什么和平年代还要进行具有许多新的历史特点的伟大斗争？</div>', null, 'https://p2.img.cctvpic.com/photoAlbum/page/performance/img/2021/4/22/1619095833656_322.jpg');
INSERT INTO `tab_book` VALUES ('4', '毛泽东 邓小平 江泽民 胡锦涛关于中国共产党历史论述摘编', '《毛泽东邓小平江泽民胡锦涛关于中国共产党历史论述摘编》（以下简称《论述摘编》）共计141段论述，分别摘自毛泽东同志、邓小平同志、江泽民同志、胡锦涛同志的讲话、报告、谈话、批示和书信等重要文献，内容涵盖对我们党不同时期的历史回顾，特别是对党史上重要人物、重要会议、重大事件的纪念及总结，深刻阐释了党在各个历史时期的光辉历程、理论成果、宝贵经验和伟大精神。', '<div>毛泽东重要论述（上）</div>\r\n<div>毛泽东重要论述（中）</div>\r\n<div>毛泽东重要论述（下）</div>\r\n<div>邓小平重要论述（上）</div>\r\n<div>邓小平重要论述（下）</div>\r\n<div>江泽民重要论述（上）</div>\r\n<div>江泽民重要论述（下）</div>\r\n<div>胡锦涛重要论述（上）</div>\r\n<div>胡锦涛重要论述（下）</div>', null, 'https://p5.img.cctvpic.com/photoAlbum/page/performance/img/2021/4/22/1619094178140_891.jpg');
INSERT INTO `tab_book` VALUES ('5', '中华人民共和国简史', '《中华人民共和国简史》坚持马克思主义唯物史观和实事求是原则，以习近平新时代中国特色社会主义思想为指导，在充分占有历史资料的基础上，去伪存真、去粗取精，科学、准确、全面地阐释历史。该书在写作过程中，注意充分吸收学界最新研究成果，并多次征求有关部门意见。这部著作是党史学习教育的重要参考材料，也是全社会开展“四史”宣传教育的重要用书。', null, null, 'https://p4.img.cctvpic.com/photoAlbum/page/performance/img/2021/10/12/1634001209686_194.jpg');
INSERT INTO `tab_book` VALUES ('6', '改革开放简史', '《简史》以习近平新时代中国特色社会主义思想为指导，全面贯彻习近平总书记关于“四史”的重要论述，充分体现习近平总书记在庆祝改革开放40周年大会、庆祝中华人民共和国成立70周年大会、党史学习教育动员大会、庆祝中国共产党成立100周年大会等会议上的重要讲话精神，以党中央有关文件精神为依据，牢牢把握改革开放40多年的主题和主线、主流和本质，站在实现“两个一百年”奋斗目标、实现中华民族伟大复兴的高度，站在历史和时代进步、党和国家未来发展的高度，以准确、系统、完整、生动、可读为写作原则，以严谨、流畅、受读者欢迎为写作目标，突出主题主线，注重夹叙夹议、史论结合，力求准确、简明阐述我国改革开放40多年的壮阔实践史，深刻、辩证、重点概括40多年改革开放蕴涵的丰富治国理政智慧和历史经验。', null, null, 'https://p1.img.cctvpic.com/photoAlbum/page/performance/img/2021/10/12/1634001187780_236.jpg');
INSERT INTO `tab_book` VALUES ('111', '社会主义发展简史', '　《社会主义发展简史》是按照党中央统一部署，为庆祝中国共产党成立100周年，配合全党开展党史学习教育，由中宣部组织有关单位编写的。编写组认真学习贯彻习近平总书记关于社会主义发展史一系列重要讲话精神和关于学习“四史”的重要要求，紧紧围绕社会主义发展的历史主题、历史脉络、历史规律，紧紧围绕中国特色社会主义的理论逻辑、历史逻辑、实践逻辑，深入阐述社会主义发展宏阔壮丽的历史进程，努力做到导向正确、史实准确、观点鲜明、语言生动，使之成为一部适合全党特别是基层党员学习，同时适合青年学生和普通群众了解社会主义发展史的高质量辅导读物。', null, null, 'https://p1.img.cctvpic.com/photoAlbum/page/performance/img/2021/10/12/1634001169215_927.jpg');
INSERT INTO `tab_book` VALUES ('112', '中国共产党的九十年', '经中共中央批准，中共中央党史研究室编著的《中国共产党的九十年》，由中共党史出版社、党建读物出版社出版发行。\r\n\r\n　　2010年7月，习近平同志提出编写《中国共产党的九十年》的任务，并在编写工作启动后亲自审定编写工作方案，作出长篇重要批示，给予重要指导。本书在中共中央党史研究室室务委员会具体领导下，历时六年编写完成。在编撰过程中，注意吸收《中国共产党历史》第一卷、第二卷和《中国共产党的七十年》的精华，并积极吸收党史研究最新成果。\r\n\r\n　　在本书编撰过程中，恰逢中国共产党第十八次全国代表大会召开，为了记述内容的完整性，将时间下限向后顺延，反映了1921—2012年中国共产党的历史。全书分为新民主主义革命时期、社会主义革命和建设时期、改革开放和社会主义现代化建设新时期三册，共60余万字。本书随文插图400余幅，图文并茂、准确生动地展现了中国共产党90余年的奋斗历程、光辉业绩和取得的伟大成就。\r\n\r\n　　这部权威通史类党史基本著作的出版，是党史学界取得的重大研究成果，也为广大党员、干部、群众和青少年学习党史提供了一部重要的教科书。', null, null, 'https://p4.img.cctvpic.com/photoAlbum/page/performance/img/2021/4/9/1617938414447_468.jpg');
INSERT INTO `tab_book` VALUES ('113', '红军长征史', '在两年的时间中，各路红军以百折不挠的革命精神和所向无敌的英雄气概，翻越了陡峭险峻甚至终年积雪的座座高山，跨过了激流汹涌、难以渡过的条条江河，穿过了沼泽遍布、神秘莫测的茫茫草地，冲出了国民党数十万军队一层又一层、一次又一次的围追堵截；他们顶寒风，冒雨雪，忍饥挨饿，遭冷受冻，甚至吃草根、咽树皮，经受了难以想象的千辛万苦，终于赢得了长征的胜利。\r\n\r\n　　由中共中央党史研究室第一研究部编著，郭德宏、张树军、韩钢、徐占权等著名党史军史专家撰写的这部《红军长征史》，真实地再现了这个人类历史上的伟大奇迹。作为一部经典的权威史著，本书20年前出版后影响很大，受到读者广泛关注，多次重印和再版，并曾获得国家图书奖和“五个一工程”奖。为弘扬长征精神，时值红军长征胜利80周年，《红军长征史》修订重印。', null, null, 'https://p3.img.cctvpic.com/photoAlbum/page/performance/img/2021/4/9/1617938531484_731.jpg');
INSERT INTO `tab_book` VALUES ('114', '新征程面对面', '《新征程面对面》以习近平新时代中国特色社会主义思想为指导，紧密联系我国进入新发展阶段实际，紧密联系干部群众思想实际，从理论阐释、新闻视角、问题意识、语言特色、历史纵深、国际视野等维度，对这些重大问题作出了深入浅出的解读，观点权威准确，语言通俗易懂，文风清新简洁，形式活泼多样，既讲是什么又讲为什么，既讲怎么看又讲怎么办，可作为干部群众、青年学生进行理论学习和开展形势政策教育的重要辅助读物。', '<div>一、复兴伟业谱新篇——怎么看新发展阶段？</div>\r\n<div>二、凌云纵横绘蓝图——新征程如何开启？</div>\r\n<div>三、天高海阔观澎湃——为什么我国发展仍然处于重要战略机遇期？</div>\r\n<div>四、破茧成蝶求嬗变——为什么要以推动高质量发展为主题？</div>\r\n<div>五、观念一变天地宽——新发展理念如何完整准确全面贯彻？</div>\r\n<div>六、一招落子满盘活——新发展格局如何加快构建？</div>\r\n<div>七、宝剑锋从磨砺出——科技自立自强如何实现？</div>\r\n<div>八、阡陌乡间大可为——农业农村现代化如何推进？</div>\r\n<div>九、彩云长在有新天——国家文化软实力如何提高？</div>\r\n<div>十、海纳百川千帆竞——合作共赢新局面如何开拓？</div>\r\n<div>十一、汔可小康盼大同——共同富裕如何扎实推动？</div>\r\n<div>十二、居安思危多备虑——发展和安全如何统筹？</div>\r\n<div>十三、运筹帷幄谋全局——为何强调坚持系统观念？</div>\r\n<div>十四、奋楫扬帆逐浪高——新发展奇迹如何创造？</div>', null, 'https://p2.img.cctvpic.com/photoAlbum/page/performance/img/2021/10/12/1634001464097_949.jpg');
INSERT INTO `tab_book` VALUES ('115', '中国制度面对面', '《中国制度面对面》以习近平新时代中国特色社会主义思想为指导，紧密联系新时代中国特色社会主义制度建设实际，紧密联系干部群众思想实际，对中国特色社会主义制度是怎么来的、中国特色社会主义制度为什么好、中国特色社会主义制度如何行稳致远等16个重大问题进行了深入浅出的解读阐释，可作为干部群众、青年学生进行理论学习和开展形势政策教育的重要辅助读物。', '<div>一、千秋伟业铸巨制——中国特色社会主义制度是怎么来的？</div>\r\n<div>二、喜看稻菽千重浪——中国特色社会主义制度为什么好？</div>\r\n<div>三、沧海横流显砥柱——党的领导制度体系为何摆在首位？</div>\r\n<div>四、神州人人皆舜尧——人民当家作主怎样通过制度来实现？</div>\r\n<div>五、治国安邦倚重器——中国特色社会主义法治体系如何保证公平正义？</div>\r\n<div>六、高效协同更便民——中国特色社会主义行政体制如何优化？</div>\r\n<div>七、经世济民正其制——社会主义基本经济制度有何重大发展？</div>\r\n<div>八、凝心聚神谱新篇——社会主义先进文化制度怎么守正创新？</div>\r\n<div>九、惟愿苍生俱饱暖——民生保障制度如何惠及全体人民？</div>\r\n<div>十、同心共筑平安梦——共建共治共享的社会治理制度如何搭建？</div>\r\n<div>十一、家在青山绿水间——生态文明制度体系如何为美丽中国保驾护航？</div>\r\n<div>十二、铁血丹心铸军魂——党对人民军队的绝对领导制度为何动摇不得？</div>\r\n<div>十三 、历史大势不可挡——“一国两制”重要制度为什么完全行得通？</div>\r\n<div>十四、命运与共同凉热——和平外交政策如何营造良好国际环境？</div>\r\n<div>十五、激浊扬清织密网——党和国家监督体系如何健全完善？</div>\r\n<div>十六、直挂云帆济沧海——中国特色社会主义制度如何行稳致远？</div>\r\n<div>一、千秋伟业铸巨制——中国特色社会主义制度是怎么来的？</div>\r\n<div>二、喜看稻菽千重浪——中国特色社会主义制度为什么好？</div>\r\n<div>三、沧海横流显砥柱——党的领导制度体系为何摆在首位？</div>\r\n<div>四、神州人人皆舜尧——人民当家作主怎样通过制度来实现？</div>\r\n<div>五、治国安邦倚重器——中国特色社会主义法治体系如何保证公平正义？</div>\r\n<div>六、高效协同更便民——中国特色社会主义行政体制如何优化？</div>\r\n<div>七、经世济民正其制——社会主义基本经济制度有何重大发展？</div>\r\n<div>八、凝心聚神谱新篇——社会主义先进文化制度怎么守正创新？</div>\r\n<div>九、惟愿苍生俱饱暖——民生保障制度如何惠及全体人民？</div>\r\n<div>十、同心共筑平安梦——共建共治共享的社会治理制度如何搭建？</div>\r\n<div>十一、家在青山绿水间——生态文明制度体系如何为美丽中国保驾护航？</div>\r\n<div>十二、铁血丹心铸军魂——党对人民军队的绝对领导制度为何动摇不得？</div>\r\n<div>十三 、历史大势不可挡——“一国两制”重要制度为什么完全行得通？</div>\r\n<div>十四、命运与共同凉热——和平外交政策如何营造良好国际环境？</div>\r\n<div>十五、激浊扬清织密网——党和国家监督体系如何健全完善？</div>\r\n<div>十六、直挂云帆济沧海——中国特色社会主义制度如何行稳致远？</div>\r\n', null, 'https://p2.img.cctvpic.com/photoAlbum/page/performance/img/2021/4/9/1617939096849_226.jpg');
INSERT INTO `tab_book` VALUES ('116', '新中国发展面对面', '该书以习近平新时代中国特色社会主义思想为指导，紧密联系新中国社会主义现代化建设的伟大实践，紧密联系干部群众思想实际，从理论与实践、历史与现实、国内与国际的结合上，对这些重大问题作出了深入浅出的解读阐释，集中回答干部群众普遍关心的重大问题。', '<div>天翻地覆慨而慷——新中国70年发生了怎样的变化</div>\r\n<div>当惊世界殊——中国经济奇迹是如何创造的？</div>\r\n<div>治不必同期于利民——中国式民主为什么符合国情</div>\r\n<div>文脉同国脉相连——中国何以文化自信</div>\r\n<div>幸福是奋斗出来的——中国老百姓日子怎样越过越红火</div>\r\n<div>生态兴则文明兴——中国是怎样走上绿色发展之路的</div>\r\n<div>锻造坚不可摧的钢铁长城——中国国防和军队现代化是如何推进的</div>\r\n<div>血浓于水一家亲——中国是如何坚持“一国两制”和推进祖国统一的</div>\r\n<div>为世界谋大同——中国是怎样走近世界舞台中央的</div>\r\n<div>鞋子合不合脚穿着才知道——中国道路为什么好</div>\r\n<div>众星拱月玉宇澈——中国共产党为什么能</div>\r\n<div>九万里风鹏正举——中国未来为什么前景可期</div>\r\n<div>天翻地覆慨而慷——新中国70年发生了怎样的变化</div>\r\n<div>当惊世界殊——中国经济奇迹是如何创造的？</div>\r\n<div>治不必同期于利民——中国式民主为什么符合国情</div>\r\n<div>文脉同国脉相连——中国何以文化自信</div>\r\n<div>幸福是奋斗出来的——中国老百姓日子怎样越过越红火</div>\r\n<div>生态兴则文明兴——中国是怎样走上绿色发展之路的</div>\r\n<div>锻造坚不可摧的钢铁长城——中国国防和军队现代化是如何推进的</div>\r\n<div>血浓于水一家亲——中国是如何坚持“一国两制”和推进祖国统一的</div>\r\n<div>为世界谋大同——中国是怎样走近世界舞台中央的</div>\r\n<div>鞋子合不合脚穿着才知道——中国道路为什么好</div>\r\n<div>众星拱月玉宇澈——中国共产党为什么能</div>\r\n<div>九万里风鹏正举——中国未来为什么前景可期</div>\r\n<div>天翻地覆慨而慷——新中国70年发生了怎样的变化</div>\r\n<div>当惊世界殊——中国经济奇迹是如何创造的？</div>\r\n<div>治不必同期于利民——中国式民主为什么符合国情</div>\r\n<div>文脉同国脉相连——中国何以文化自信</div>\r\n<div>幸福是奋斗出来的——中国老百姓日子怎样越过越红火</div>\r\n<div>生态兴则文明兴——中国是怎样走上绿色发展之路的</div>\r\n<div>锻造坚不可摧的钢铁长城——中国国防和军队现代化是如何推进的</div>\r\n<div>血浓于水一家亲——中国是如何坚持“一国两制”和推进祖国统一的</div>\r\n<div>为世界谋大同——中国是怎样走近世界舞台中央的</div>\r\n<div>鞋子合不合脚穿着才知道——中国道路为什么好</div>\r\n<div>众星拱月玉宇澈——中国共产党为什么能</div>\r\n<div>九万里风鹏正举——中国未来为什么前景可期</div>\r\n', '', 'https://p4.img.cctvpic.com/photoAlbum/page/performance/img/2021/4/9/1617938601355_921.jpg');

