  KEY `user_id` (`user_id`),
  KEY `detail_id` (`detail_id`),
  KEY `reply_id` (`reply_id`),
  CONSTRAINT `tab_comment_ibfk_2` FOREIGN KEY (`user_id`) REFERENCES `tab_user` (`user_id`) ON DELETE CASCADE ON UPDATE CASCADE,
