-- ----------------------------
-- Table structure for `tab_detail`
-- ----------------------------
DROP TABLE IF EXISTS `tab_detail`;
CREATE TABLE `tab_detail` (
  `detail_id` int(11) NOT NULL AUTO_INCREMENT,
  `title` varchar(255) DEFAULT NULL,
  `author` varchar(255) DEFAULT NULL,
  `time` varchar(255) DEFAULT '',
  `views` varchar(255) DEFAULT '0',
  `content` text,
  `likes` varchar(255) DEFAULT '0',
  `comments` varchar(255) DEFAULT '0',
  `category_id` int(11) DEFAULT NULL,
  `type` tinyint(4) DEFAULT '0',
  PRIMARY KEY (`detail_id`),
  KEY `category_id` (`category_id`),
  CONSTRAINT `tab_detail_ibfk_1` FOREIGN KEY (`category_id`) REFERENCES `tab_category` (`category_id`) ON DELETE CASCADE ON UPDATE CASCADE
) ENGINE=InnoDB AUTO_INCREMENT=18 DEFAULT CHARSET=utf8;

